package Exercises;

public class QuartsToGallons {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int quartsInGallon, quartsNeeded;
		quartsInGallon = 4;
		quartsNeeded = 18;
		System.out.print("You need " + quartsNeeded / quartsInGallon + " gallons and " + quartsNeeded % quartsInGallon + " quarts");
	}

}
